#include <stdio.h>

int main() {
    int h, m;          // 현재 시간
    int add_h, add_m;  // 추가 시간

    // 입력 받기
    scanf("%d %d", &h, &m);
    scanf("%d %d", &add_h, &add_m);

    // 분 먼저 더하기
    m += add_m;

    // 분이 60 이상이면 시로 넘기기
    if (m >= 60) {
        h += m / 60;
        m = m % 60;
    }

    // 시 더하기
    h += add_h;

    // 시가 24 이상이면 0부터 다시 시작
    if (h >= 24) {
        h = h % 24;
    }

    // 결과 출력
    printf("%d %d\n", h, m);

    return 0;
}
